package com.example.region.config;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotEmpty;import java.util.List;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.validation.annotation.Validated;@ConfigurationProperties(prefix = "region")@Validatedpublic class RegionProperties {    @NotBlank    private String code;    @NotBlank    private String environment;    @NotBlank    private String timezone;    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getEnvironment() {        return environment;    }    public void setEnvironment(String environment) {        this.environment = environment;    }    public String getTimezone() {        return timezone;    }    public void setTimezone(String timezone) {        this.timezone = timezone;    }}